import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [isAdding, setIsAdding] = useState(false);

  const addTask = () => {
    if (newTask.trim() !== '') {
      setIsAdding(true);
      const newTaskObj = { 
        id: Date.now(), 
        text: newTask.trim(), 
        completed: false,
        isNew: true
      };
      
      setTasks([...tasks, newTaskObj]);
      setNewTask('');
      
      // Remove the new class after animation
      setTimeout(() => {
        setTasks(prevTasks => 
          prevTasks.map(task => 
            task.id === newTaskObj.id ? { ...task, isNew: false } : task
          )
        );
        setIsAdding(false);
      }, 500);
    }
  };

  const toggleTask = (id) => {
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  const deleteTask = (id) => {
    // Add exit animation
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, isDeleting: true } : task
    ));
    
    // Remove task after animation
    setTimeout(() => {
      setTasks(tasks.filter(task => task.id !== id));
    }, 300);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTask();
    }
  };

  return (
    <div className="app">
      <div className="container">
        <h1>My To-Do List</h1>
        
        <div className="input-section">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Add a new task..."
            className="task-input"
            disabled={isAdding}
          />
          <button 
            onClick={addTask} 
            className={`add-button ${isAdding ? 'adding' : ''}`}
            disabled={isAdding || !newTask.trim()}
          >
            {isAdding ? 'Adding...' : 'Add Task'}
          </button>
        </div>

        <div className="tasks-section">
          {tasks.length === 0 ? (
            <p className="no-tasks">No tasks yet. Add one above!</p>
          ) : (
            <ul className="task-list">
              {tasks.map(task => (
                <li 
                  key={task.id} 
                  className={`task-item ${task.completed ? 'completed' : ''} ${task.isNew ? 'new-task' : ''} ${task.isDeleting ? 'deleting' : ''}`}
                >
                  <span 
                    className="task-text"
                    onClick={() => toggleTask(task.id)}
                  >
                    {task.text}
                  </span>
                  <button 
                    onClick={() => deleteTask(task.id)}
                    className="delete-button"
                    disabled={task.isDeleting}
                  >
                    {task.isDeleting ? 'Deleting...' : 'Delete'}
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        {tasks.length > 0 && (
          <div className="stats">
            <p>
              Total: {tasks.length} | 
              Completed: {tasks.filter(task => task.completed).length} | 
              Remaining: {tasks.filter(task => !task.completed).length}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
